  /**
   * @param {File} file 处理的文件
   * @param {boolean} [isDownload=false] 是否下载，如果是下载，返回buffer
   * @param {*} options
   * {
   *    resize: [100, 100],
   *    format: 'png', // png jpg
   *    trim: true, // true/false
   * }
   */
  async process(file, options, isDownload = false, resp) {
    const res = new Response();
    if (!isImageFile(file)) {
      return res.fail('只处理图片');
    }
    let animated = false;
    if (isGIFImage(file)) {
      animated = true;
    }
    let sharpFile = sharp(file.buffer, { animated, limitInputPixels: false });
    // 大小
    const resize = options?.resize?.split(',');
    if (resize?.length) {
      if (resize.length === 1) {
        sharpFile = sharpFile.resize({ width: +resize[0], fit: 'fill' });
      } else if (resize.length === 2) {
        sharpFile = sharpFile.resize({ width: +resize[0], height: +resize[1], fit: 'fill' });
      }
    }
    // 格式
    if (options.format === 'jpg') {
      sharpFile = sharpFile.jpeg();
    } else if (options.format === 'png') {
      sharpFile = sharpFile.png();
    } else if (options.format === 'webp') {
      sharpFile = sharpFile.webp();
    }
    // 去空白
    if (options?.trim === '1') {
      sharpFile = sharpFile.trim();
    }

    const buffer = await sharpFile.toBuffer();
    if (isDownload) {
      resp.set('content-disposition', `${this.getBasename(file.originalname)}-${dayjs().format('YYYYMMDDHHmmss')}.${options.format}`);
      return buffer;
    }
    return res.success(buffer.toString('base64'));
  }

